insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (1, '外科', '外科，治疗各种外科疾病', '2020-07-27', -1, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (2, '内科', '内科，治疗各种内科', '2020-07-27', -1, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (3, '泌尿外科', '属于外科，各种尿结石和复杂性肾结石；肾脏和膀胱肿瘤；前列腺增生和前列腺炎；睾丸附睾的炎症和肿瘤；睾丸精索鞘膜积液；各种泌尿系损伤；泌尿系先天性畸形如尿道下裂、隐睾、肾盂输尿管连接部狭窄所导致的肾积水等等。', '2020-07-28', 1, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (4, '肾内科', '属于内科，肾脏病是常见病和多发病，如果恶化可以发展为尿毒症，严重危害人们健康。慢性肾脏病已成为继心脑血管病、肿瘤、糖尿病之后又一种威胁人类健康的重要疾病，成为全球性公共卫生问题。', '2020-07-28', 2, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (5, '妇产科', '妇产科分为妇科、产科', '2020-07-27', -1, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (6, '儿科', '儿科，治疗各种儿科疾病', '2020-07-27', -1, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (7, '皮肤科', '皮肤科，治疗各种皮肤疾病', '2020-07-27', -1, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (8, '眼科', '眼科，治疗各种眼科疾病', '2020-07-27', -1, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (9, '耳鼻喉科', '耳鼻咽喉科，治疗各种耳鼻咽喉疾病', '2020-07-27', -1, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (10, '口腔科', '口腔科，治疗各种口腔疾病
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (11, '肿瘤科', '肿瘤科和内科、外科、妇产科和儿科一样，是临床医学的二级学科，分为肿瘤内科、肿瘤放射治疗科和肿瘤外科等，肿瘤内科主要从事各种良、恶性肿瘤的内科治疗；肿瘤放射治疗科主要从事肿瘤的放射线治疗；肿瘤外科提供以手术为主的综合治疗。专门的肿瘤医院的相关科室会根据不同部位再行细分，例如肿瘤内科里的胃肠肿瘤科、淋巴瘤肿瘤科；肿瘤外科有乳腺外科、头颈外科、胸外科、肿瘤妇科、腹部外科等。', '2020-07-27', -1, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (12, '中医科', '中医科采用中药治疗各种冠心病、心律失常、脑梗塞、脑动脉硬化、顽固性头痛、急慢性肾炎、泌尿系结石、男科病、脾胃病、糖尿病以及心身疾病。运用针灸、理疗等方法治疗中风、偏瘫、风湿性关节炎、哮喘等疾病，开设针灸减肥门诊，为广大肥胖患者减轻痛苦。微电脑牵引仪治疗颈椎病、腰椎增生及腰椎间盘突出症，效果显著', '2020-07-27', -1, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (13, '妇科', '妇科是医疗机构的一个诊疗科目，妇科是妇产科的一个分支专业，是以诊疗女性妇科病为诊疗的专业科室，分为西医妇科与中医妇科。妇科疾病包括：女性生殖系统的疾病即为妇科疾病，包括外阴疾病、阴道疾病、子宫疾病、输卵管疾病、卵巢疾病等', '2020-07-28', 5, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (14, '产科', '产科是临床医学四大主要学科之一，主要研究女性生殖器官疾病的病因、病理、诊断及防治，妊娠、分娩的生理和病理变化，高危妊娠及难产的预防和诊治，女性生殖内分分泌，计划生育及妇女保健等。', '2020-07-28', 5, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (15, '儿科门诊', '对儿童的进行分析的门诊部', '2020-07-28', 6, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (16, '儿科保健科', '对儿童的身心健康保健的科系', '2020-07-28', 6, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (17, '皮肤美容科', '皮肤美容科，通常指医学上，通过专业治疗与养生等方式，使人体皮肤达到美容保养的效果。皮肤美容，通常指一种改变原有的不良行为和疾病（面部或皮肤），使之成为文明的、高素质的、具有可以被人接受的外观形象有活动和过程，或为达到此目的而使用的产品和方法。', '2020-07-28', 7, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (18, '损伤性皮肤病科', '损伤性皮肤病科，进行皮肤的专业整治', '2020-07-28', 7, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (19, '眼科门诊', '眼科的全称是“眼病专科”，是研究发生在视觉系统，包括眼球及与其相关联的组织有关疾病的学科。眼科一般研究玻璃体、视网膜疾病，眼视光学，青光眼和视神经病变，白内障等多种眼科疾病。', '2020-07-28', 8, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (20, '口鼻喉科门诊', '“耳鼻咽喉科”是诊断治疗耳、鼻、咽、喉、及其相关头颈区域的外科学科。', '2020-07-28', 9, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (21, '口腔科门诊', '口腔科，医学学科分类之一。主要口腔科疾病包括：口腔颌面部皮样、表皮颌下间隙感染、颌面部淋巴管瘤、齿状突发育畸形、上颌窦恶性肿瘤、颌骨造釉细胞瘤、慢性筛窦炎、下颌后缩、四环素牙、舌白斑等疾病。通过技术，许多牙周病完全可以治愈。
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (22, '肿瘤科门诊', '肿瘤科和内科、外科、妇产科和儿科一样，是临床医学的二级学科，分为肿瘤内科、肿瘤放射治疗科和肿瘤外科等，肿瘤内科主要从事各种良、恶性肿瘤的内科治疗；肿瘤放射治疗科主要从事肿瘤的放射线治疗；肿瘤外科提供以手术为主的综合治疗。专门的肿瘤医院的相关科室会根据不同部位再行细分，例如肿瘤内科里的胃肠肿瘤科、淋巴瘤肿瘤科；肿瘤外科有乳腺外科、头颈外科、胸外科、肿瘤妇科、腹部外科等。', '2020-07-28', 11, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (23, '中医科门诊', '口腔科，医学学科分类之一。主要口腔科疾病包括：口腔颌面部皮样、表皮颌下间隙感染、颌面部淋巴管瘤、齿状突发育畸形、上颌窦恶性肿瘤、颌骨造釉细胞瘤、慢性筛窦炎、下颌后缩、四环素牙、舌白斑等疾病。通过技术，许多牙周病完全可以治愈。
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (24, '针灸门诊', '属于中医科', '2020-07-28', 12, 18899996666);
insert into hors.departments (depart_id, depart_name, introduce, date, d_id, phone) values (27, '胸外科', '<p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;">河南省人民医院胸外科是河南省重点（培育）临床学科，先后被国家人事部、卫生部授予“全国卫生战线先进集体”等称号，是河南省肺、食管、纵隔及胸壁疾病的治疗中心。现有主任医师6人、副主任医师6人，其中博士学位4人、硕士学位16人。科室包括胸外科、河南省胸部肿瘤诊疗中心、河南省肺移植中心三部分；拥有设施完善的监护病房以及飞利浦V60无创呼吸机、奥林巴斯纤维支气管镜、丹麦ABL500多功能血气分析仪、德国STORZ 3D电视胸腔镜等先进仪器，为患者提供胸科疾病的诊断、治疗、心理咨询和随访等全方位服务。</p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;"><br/></p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;">专科特色：</p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;">肺亚专科</p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;">1、肺结节（早期肺癌）诊治 &nbsp;国内首创低剂量高分辨CT灌注扫描肺结节定性、肺结节（早期肺癌）的带刻度银夹定位法，联合中国肺癌防治联盟河南省肺部结节（肿瘤）早期诊断中心，在结节（早期肺癌）筛查、诊断治疗方面积累了丰富的临床经验；常规开展胸腔镜微创手术,使更多的早期肺癌获得较好的生存。</p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;">2、 肺癌的规范化诊疗 &nbsp;河南省胸部肿瘤诊疗中心是国内最早倡导肺癌规范化治疗的学科之一，在肺癌规范化诊疗方面拥有很强实力：常规开展胸腔镜单孔肺叶切除术、全肺切除术、系统淋巴结清扫术、胸腔镜微创支气管成形术、肺动脉成形术等手术。承担多项国内药物单中心、多中心临床研究。</p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;">3、肺移植 肺移植是治疗终末期肺病的唯一治疗手段，代表了胸外科最高水平。河南省胸部肿瘤诊疗中心自2015年完成河南省首例双肺序贯移植手术以来,开创了河南胸外科发展的新起点，2016年肺移植手术量位居国内第三，制定了河南肺移植规范，承担部分国家肺移植质控，中心现常规开展单、双肺移植手术，患者远期生存率位居国内前茅。</p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;">4、常规开展肺大疱、自发性气胸、支气管扩张、肺隔离症、肺化脓症等良性疾病的外科治疗。</p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;"><br/></p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;">食管亚专科</p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;">1、食管癌的根治手术及扩大根治术在国内居于领先地位，开展包括食管次全切除术、全切除术、两野淋巴结清扫术、三野淋巴结清扫术、结肠代食管术、空场代食管术、经裂孔食管切除术等各类手术，手术切除率达到97.5 %，术后并发症率在2%以下，5年生存率国内领先。2008年起针对进展期食管癌开展的术前新辅助化放疗联合手术治疗受到国内同行的普遍关注，现已取得初步成果。</p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;">2、高龄、危重食管癌患者的手术治疗：对于高龄（80岁以上，最大手术患者91岁），合并高血压、冠心病、糖尿病的食管癌患者外科治疗有着丰富的经验，并发症发生率低，年完成该类手术100余例。常规开展胸腔镜微创食管癌切除术，具有术后并发症发生率低、创伤小、恢复快等优势。</p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;">3、针对食管裂孔疝、反流性食管炎、贲门失弛缓症、食管憩室、食管平滑肌瘤等食管良性疾病的胸腔镜微创治疗和内镜治疗在国内居于一流水平。</p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;"><br/></p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;">纵隔气管亚专科</p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;">1、胸腔镜微创纵隔肿瘤手术 胸外科于1995年率先在我省开展胸腔镜手术，常规开展胸腔镜微创纵隔良性肿瘤、畸胎瘤、各种纵隔囊肿的手术。胸腺瘤患者约40%合并重症肌无力，重症肌无力患者中约20~30%有胸腺瘤或胸腺增生，胸腔镜微创胸腺扩大切除术是胸腺瘤、重症肌无力的首选外科治疗方法。科室近年来开展了新辅助化、放疗联合手术治疗恶性胸腺瘤，恶性胸腺瘤的手术切除率达到95% ，提高了恶性胸腺瘤的手术切除率，延长了病人的生存期。</p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;">2、气管外科在气管肿瘤切除，气管重建方面积累了丰富的临床经验，是河南胸外科首个亚专业学科，居领先地位。</p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;"><br/></p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;">其他</p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;">手汗症的微创手术治疗 胸腔镜微创交感神经链切断术是目前国际上该病治疗的最佳方法，有效率在95%以上，利用腋下1~2cm小切口完成整个手术，具有创伤小、安全可靠、术后恢复快等优点。我科是我省开展这种手术最多、最好的单位。</p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;">漏斗胸等先天性畸形的治疗 在全省率先开展胸腔镜下NUSS胸骨抬举术矫治漏斗胸，微创、美观、住院时间短、疗效确切。</p><p style="padding: 0px; margin-top: 0px; margin-bottom: 0px; text-indent: 2em; color: rgb(102, 102, 102); font-family: Arial, Tahoma, &quot;Microsoft YaHei&quot;, SimSun; font-size: 14px; white-space: normal;">重症胸外伤的抢救和治疗 科室多次承担汶川地震救灾等重大事故的抢救任务，对于胸外伤包括多发肋骨骨折、肺挫裂伤、气管食管破裂、急性呼吸窘迫综合征、多脏器功能不全/衰竭的患者以手术为主的综合治疗具有丰富经验，抢救成功率达到国内先进水平。</p><p><br/></p>', '2001-02-03', 1, 18645458787);
