INSERT INTO departments (depart_id, depart_name, introduce, date, d_id, phone) VALUES (3, '泌尿外科', '属于外科，各种尿结石和复杂性肾结石；肾脏和膀胱肿瘤；前列腺增生和前列腺炎；睾丸附睾的炎症和肿瘤；睾丸精索鞘膜积液；各种泌尿系损伤；泌尿系先天性畸形如尿道下裂、隐睾、肾盂输尿管连接部狭窄所导致的肾积水等等。', '2020-07-28', 1, '18899996666');
INSERT INTO departments (depart_id, depart_name, introduce, date, d_id, phone) VALUES (4, '肾内科', '属于内科，肾脏病是常见病和多发病，如果恶化可以发展为尿毒症，严重危害人们健康。慢性肾脏病已成为继心脑血管病、肿瘤、糖尿病之后又一种威胁人类健康的重要疾病，成为全球性公共卫生问题。', '2020-07-28', 2, '18899996666');
