insert into hors.doctor (doctor_id, job_number, depart_id, name, sex, picture, birth, position, work_experience, phone, adept, introduce) values (3, 1003, 4, '小王', 1, '/hors/uploadfiles/thumb/201603221527067.png', '1999-07-01', '主任医师，内科主任', '工作经验3年', 13478954125, '擅长骨科', '医院标题呼吸内科科主任，1983年山西医学院医学系毕业，从事呼吸内科临床诊疗、科研、教学工 作27年。现任医院标题老年临床医学部呼吸内科主任，党总支组织委员，中华医学会呼吸病学会委员，山西省医学会呼吸专业委员会主任委员， 中国睡眠研究会理事，中国医师协会全国睡眠医学专家委员会委员，美国胸科医师协会(ACCP)资深会员(FCCP)，山西省生物医学工程学会常务理事，山西省防痨协会常务理事， 山西省中西医结合委员会委员，《中华实用诊断与治疗杂志》、《中国实用医刊杂志》常务编委、《中华结核和呼吸病杂志》通讯编委、《中华哮喘杂志》编委。国务院政府 特殊津贴享受专家。山西中医学院老年内科、山东大学硕士生导师。');
insert into hors.doctor (doctor_id, job_number, depart_id, name, sex, picture, birth, position, work_experience, phone, adept, introduce) values (4, 1004, 4, '小张', 1, '/hors/uploadfiles/thumb/201603221527067.png', '1999-08-01', '科主任', '工作经验2年', 13485954125, '擅长骨科', '1983年山西医学院医学系毕业，从事呼吸内科临床诊疗、科研、教学工 作27年。现任医院标题老年临床医学部呼吸内科主任，党总支组织委员，中华医学会呼吸病学会委员，山西省医学会呼吸专业委员会主任委员， 中国睡眠研究会理事，中国医师协会全国睡眠医学专家委员会委员，美国胸科医师协会(ACCP)资深会员(FCCP)，山西省生物医学工程学会常务理事，山西省防痨协会常务理事， 山西省中西医结合委员会委员，《中华实用诊断与治疗杂志》、《中国实用医刊杂志》常务编委、《中华结核和呼吸病杂志》通讯编委、《中华哮喘杂志》编委。国务院政府 特殊津贴享受专家。山西中医学院老年内科、山东大学硕士生导师。');
