insert into hors.doctor (doctor_id, job_number, depart_id, name, sex, picture, birth, position, work_experience, phone, adept, introduce) values (7, 1007, 13, '张文杰', 1, '/hors/pic/2012121911355172.jpg', '1999-08-09', '主任医师', '多年临床经验', 18073896477, '肿瘤及心血管核医学。 ', '医学博士，郑州大学硕士研究生导师，留学归国学者，河南省科技创新人才，现任中华医学会核医学分会肿瘤学组委员、河南省医学会核医学专业委员会副主任委员，河南省医学会内分泌专业委员会委员，河南省人民医院核医学科主任。2006-2007年曾在美国霍普金斯大学医学院研修一年，1991年至今从事核医学诊断及治疗工作。近年来发表学术论文30余篇，其中SCI收录及中华或国内核心期刊文章10余篇，承担省级科研课题2项，参与省级科研项目3项。');
insert into hors.doctor (doctor_id, job_number, depart_id, name, sex, picture, birth, position, work_experience, phone, adept, introduce) values (8, 1008, 14, '武海英', 2, '/hors/pic/2013070310531961.jpg', '2020-08-11', '主任医师', '医学博士，硕士生导师。现任河南省人民医院产科主任。任中华医学会河南妇产科分会委员、中华围产医学会河南分会副主任委员、中国促进自然分娩委员会委员，河南省妇幼保健协会高危妊娠副主任委员，河南省医疗事故鉴定委员会成员，获欧洲及国家级盆底疾病诊治资格证书', 18073896478, '擅长妊娠合并症的诊治(如前置胎盘、胎盘植入、瘢痕妊娠、妊娠期高血压疾病、妊娠合并心脏病、妊娠合并肝炎等)产科重症孕产妇救治及胎儿宫内治疗(复杂双胎的诊治，胎儿镜下激光凝固治疗双胎输血，射频减胎，宫内治疗等);擅长复发性流产，不良孕产，胎儿生长受限等的诊治', '医学博士，硕士生导师。现任河南省人民医院产科主任。任中华医学会河南妇产科分会委员、中华围产医学会河南分会副主任委员、中国促进自然分娩委员会委员，河南省妇幼保健协会高危妊娠副主任委员，河南省医疗事故鉴定委员会成员，获欧洲及国家级盆底疾病诊治资格证书。');
insert into hors.doctor (doctor_id, job_number, depart_id, name, sex, picture, birth, position, work_experience, phone, adept, introduce) values (9, 1009, 15, '高丽', 2, '/hors/pic/2013070310531961.jpg', '1999-08-09', '主任医师', '多年临床经验', 15578789898, '擅长诊治儿科各种疑难病、尤其小儿神经、癫痫，遗传代谢病、早期儿童发育、营养性疾病。', '河南省人民医院首席专家，主任医师、博士、留德，研究生导师，河南省优秀专家，中国儿科医师奖获奖者。中华医学会儿科分会委员、中华儿科学会互联网+委员会委员、河南省儿科分会主任委员、省儿科学会神经学组组长、亚太小儿神经学组成员；中国抗癫痫协会常务理事、河南省抗癫痫协会长；中国医促会儿科分会常务委员；中国优生科学协会儿童临床与保健分会常务委员；中国医师协会委员、中国医师协会儿科遗传学分会委员、河南省医师协会副会长；河南省医院协会儿科分会副主任委员；河南省病残儿鉴定委员会成员、河南省疫苗接种不良反应委员会副主任委员、河南省司法鉴定委员会委员。《中华实用儿科临床杂志》、《中国小儿急救医学》、《中华实用诊断与治疗杂志》、《癫痫杂志》、《中国优生》等杂志编委。发表相关学术论文140余篇、SCI4篇，著书4部，获省级科研成果贰等奖4项，厅级科研成果1、2等奖10余项，在研科研项目4项。');
insert into hors.doctor (doctor_id, job_number, depart_id, name, sex, picture, birth, position, work_experience, phone, adept, introduce) values (10, 1010, 16, '史长松', 1, '/hors/pic/2012121911355172.jpg', '1999-08-09', '主任医师', '主要从事儿科临床工作，擅长儿科常见病、多发病尤其是消化系统疾病、呼吸系统疾病及危重症的诊治，能正确分析及处理儿科疑难、危重病。依托河南省人民医院强大的综合实力，成功诊治了许多疑难、危重病例，', 15423235454, '主要从事儿科临床工作，擅长儿科常见病、多发病尤其是消化系统疾病、呼吸系统疾病及危重症的诊治，能正确分析及处理儿科疑难、危重病。依托河南省人民医院强大的综合实力，成功诊治了许多疑难、危重病例，', '硕士，硕士研究生导师。现任河南省人民医院儿科主任，儿童重症监护病房主任，是中国医师协会儿童重症医师分会呼吸危重症及机械通气专业委员会委员，河南省医学会重症医学分会儿科学组副组长，河南省中西医结合学会儿科分会副主任委员，河南省医学科学普及学会儿科专业委员会副主任委员，河南省医学会儿科分会委员，河南省医师协会儿科分会常务委员，河南省医学会变态反应分会委员，河南省医院协会会员，河南省预防接种异常反应鉴定专家组成员；《中华实用诊断与治疗杂志》编委。');
insert into hors.doctor (doctor_id, job_number, depart_id, name, sex, picture, birth, position, work_experience, phone, adept, introduce) values (11, 1011, 17, '张守民', 1, '/hors/pic/2012121911355172.jpg', '1999-08-10', '主任医师', '主任医师，硕士研究生导师，省级知名专家。', 18964645656, '长期致力于皮肤真菌病及激光等皮肤电外科的临床与研究，对血管瘤、湿疹皮炎、白癜风及银屑病等难治性皮肤病也有独到的治疗思路和方法。', '现任河南省人民医院皮肤科科主任、河南省医学会皮肤病学分会主任委员、河南省医师协会皮肤与性病医师协会副会长、河南省中西医结合学会皮肤病学分会副主任委员。长期致力于皮肤真菌病及激光等皮肤电外科的临床与研究，对血管瘤、湿疹皮炎、白癜风及银屑病等难治性皮肤病也有独到的治疗思路和方法。发表论文50余篇，承担《河南省头癣的菌种和药敏试验流行病学调查》等省级科研课题2项。收集大量临床资料，记录患者临床图片及详细信息10万余张。');
insert into hors.doctor (doctor_id, job_number, depart_id, name, sex, picture, birth, position, work_experience, phone, adept, introduce) values (12, 1012, 18, '陶佳涵', 1, '/hors/pic/2012121911355172.jpg', '1999-08-13', '主任医生', '长期致力于皮肤真菌病及激光等皮肤电外科的临床与研究，对血管瘤、湿疹皮炎、白癜风及银屑病等难治性皮肤病也有独到的治疗思路和方法。', 18756987412, '长期致力于皮肤真菌病及激光等皮肤电外科的临床与研究，对血管瘤、湿疹皮炎、白癜风及银屑病等难治性皮肤病也有独到的治疗思路和方法。', '现任河南省人民医院皮肤科科主任、河南省医学会皮肤病学分会主任委员、河南省医师协会皮肤与性病医师协会副会长、河南省中西医结合学会皮肤病学分会副主任委员。长期致力于皮肤真菌病及激光等皮肤电外科的临床与研究，对血管瘤、湿疹皮炎、白癜风及银屑病等难治性皮肤病也有独到的治疗思路和方法。发表论文50余篇，承担《河南省头癣的菌种和药敏试验流行病学调查》等省级科研课题2项。收集大量临床资料，记录患者临床图片及详细信息10万余张');
insert into hors.doctor (doctor_id, job_number, depart_id, name, sex, picture, birth, position, work_experience, phone, adept, introduce) values (13, 1013, 19, '朱良付', 1, '/hors/pic/2012121911355172.jpg', '1999-08-30', '主任医生', '朱良付，，主任医师，硕导，脑血管病急救学组负责人。神经内科教研室副主任，国家卫计委脑卒中筛查及防治工程中青年专家委员会常务委员，中国抗衰老促进会神经疾病专业委员会常务委员兼副总干事，中国医师协会神经内科分会神经介入专业委员会委员，中国老年医学学会脑血管病分会委员，中国卒中学会脑血流和代谢委员会和介入委员会委员，河南省介入治疗专业委员会副主任委员，河南省脑卒中分会副主任委员，河南省神经内科分会常务委员，河南省中西医结合头痛分会常务委员，河南省介入技术管理专家委员会委员，河南省医师协会神经重症学组委员，获评国家卫计委脑卒中防治工程优秀中青年专家奖。', 15645456565, '朱良付，，主任医师，硕导，脑血管病急救学组负责人。神经内科教研室副主任，国家卫计委脑卒中筛查及防治工程中青年专家委员会常务委员，中国抗衰老促进会神经疾病专业委员会常务委员兼副总干事，中国医师协会神经内科分会神经介入专业委员会委员，中国老年医学学会脑血管病分会委员，中国卒中学会脑血流和代谢委员会和介入委员会委员，河南省介入治疗专业委员会副主任委员，河南省脑卒中分会副主任委员，河南省神经内科分会常务委员，河南省中西医结合头痛分会常务委员，河南省介入技术管理专家委员会委员，河南省医师协会神经重症学组委员，获评国家卫计委脑卒中防治工程优秀中青年专家奖。', '河南省科技创新人才。广州中山大学附属一院神经内科脑血管病专业硕博连读毕业，在美国加州大学洛杉矶分校卒中中心（UCLA stroke center）学习一年余。擅长脑血管病的内科防治和介入诊疗（如脑血管造影、动静脉溶栓、取栓、颈动脉和颅内血管支架成形、颅内动脉瘤介入栓塞等）。熟悉神经系统炎症、脱髓鞘病、头晕、头痛、睡眠障碍、变性病、遗传病、癫痫病等常见病诊疗。已参与发表国内外论文40余篇，主持和参与省级及国家级科研基金多项。');
insert into hors.doctor (doctor_id, job_number, depart_id, name, sex, picture, birth, position, work_experience, phone, adept, introduce) values (14, 1014, 3, '丁德刚', 1, '/hors/pic/2012121911355172.jpg', '1999-07-09', '主任医师', '泌尿外科科主任，郑州大学、河南大学硕士研究生导师，河南省干部保健专家组成员，留法归国人员。1988年中山医科大学6年制本科，华中科技大学同济医学院博士。', 18978745478, '中华医学会器官移植分会委员，河南省医师协会泌尿外科医师分会会长，河南省医学会泌外专业分会副主委，河南省医学会器官移植分会副主委，河南微创外科学会常委兼泌尿学组副组长。《中华移植杂志（电子版）》、《器官移植》、《微创》、《中华实用诊断与治疗》、《河南外科》等杂志编委。', '具有扎实的理论基础，对泌尿生殖系统肿瘤、结石、前列腺疾病、肾移植及先天性畸形等疾病的诊治具有丰富的诊治经验。临床擅长泌尿生殖系肿瘤、前列腺疾病、肾移植的诊治，在泌尿微创尤其腹腔镜领域填补了省内多项空白：微创131I粒子植入治疗前列腺癌、后腹腔镜下肾部分切除术、前腹腔镜下前列腺癌根治性切除术、后腹腔镜下活体供肾切取术、腹腔镜下膀胱肿瘤根治术等均为省内首例开展。其中“后腹腔镜低温气腹活体供肾切取术”获省卫生厅科技成果一等奖、省科技成果二等奖。发表专业学术论文30余篇，获厅级以上成果奖5项，出版专著2部《实用泌尿生殖系肿瘤学》、《实用临床外科学》。主持国家人事部国际合作项目、省科技厅、省卫生厅科技项目各一项。');
insert into hors.doctor (doctor_id, job_number, depart_id, name, sex, picture, birth, position, work_experience, phone, adept, introduce) values (15, 1015, 3, '刘中华', 1, '/hors/pic/2012121911355172.jpg', '1999-08-09', '主任医生', '多年临床经验', 18079894578, '擅长泌尿外科', '河南省人民医院泌尿外科副主任、主任医师、硕士研究生导师。省抗癌协会泌尿肿瘤专业委员会名誉主委、省医师协会泌尿外科分会副会长、省医学会泌尿外科分会常委。河南省学术技术带头人、河南省知名专家。曾任省小儿外科学分会、器官移植分会委员。主编专著一部，获专利二项，发表论文近三十篇，科研成果四项，其中《带蒂包皮双面皮瓣的设计与临床应用》获省科技进步二等奖。主要研究方向为尿道下裂与尿道狭窄的手术治疗。');
insert into hors.doctor (doctor_id, job_number, depart_id, name, sex, picture, birth, position, work_experience, phone, adept, introduce) values (16, 1016, 19, '罗瑶', 2, '/hors/pic/2013070310531961.jpg', '1999-08-09', '主任医生', '多年临床经验', 18079894570, '中华医学会器官移植分会委员，河南省医师协会泌尿外科医师分会会长，河南省医学会泌外专业分会副主委，河南省医学会器官移植分会副主委，河南微创外科学会常委兼泌尿学组副组长。《中华移植杂志（电子版）》、《器官移植》、《微创》、《中华实用诊断与治疗》、《河南外科》等杂志编委。', '硕士，硕士研究生导师。现任河南省人民医院儿科主任，儿童重症监护病房主任，是中国医师协会儿童重症医师分会呼吸危重症及机械通气专业委员会委员，河南省医学会重症医学分会儿科学组副组长，河南省中西医结合学会儿科分会副主任委员，河南省医学科学普及学会儿科专业委员会副主任委员，河南省医学会儿科分会委员，河南省医师协会儿科分会常务委员，河南省医学会变态反应分会委员，河南省医院协会会员，河南省预防接种异常反应鉴定专家组成员；《中华实用诊断与治疗杂志》编委。');
